DROP TABLE IF EXISTS `address`;
CREATE TABLE `address`  (
  `Aid` int(10) NOT NULL AUTO_INCREMENT,
  `Uid` int(10) NOT NULL,
  `Areceivername` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Aaddress` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Acode` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Aphone` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Afixphone` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Aprovince` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Acity` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Atown` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Acheck` tinyint(2) NOT NULL,
  PRIMARY KEY (`Aid`) USING BTREE,
  INDEX `A_FUid`(`Uid`) USING BTREE,
  CONSTRAINT `A_FUid` FOREIGN KEY (`Uid`) REFERENCES `user` (`Uid`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 15 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

