-- ----------------------------
-- Table structure for book
-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book`  (
  `Bid` int(10) NOT NULL AUTO_INCREMENT,
  `Bimage` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Bprice` float(10, 2) NOT NULL,
  `Bname` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Btype` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '0',
  `Sid` int(10) NOT NULL,
  `Bauthor` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Bpublisher` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Bpublishdate` date DEFAULT '2005-11-08',
  `Bsalednum` int(5) UNSIGNED ZEROFILL DEFAULT NULL,
  `Bcommentnum` int(5) UNSIGNED ZEROFILL DEFAULT NULL,
  `Boriprice` float(10, 2) DEFAULT 0.00,
  `Bversion` int(5) DEFAULT 0,
  `Bpagenum` int(11) DEFAULT 0,
  `Bwordnum` int(11) DEFAULT 0,
  `Bprintdate` date DEFAULT '2005-11-08',
  `Bsize` int(5) DEFAULT 0,
  `Bpaperstyle` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Bprintnum` int(5) DEFAULT NULL,
  `Bpackage` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Bisbn` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Bcontentsummary` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '暂无',
  `Bauthorsummary` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '暂无',
  `Bmediacomment` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '暂无',
  `Btastecontent` varchar(8000) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '暂无',
  `Bstocknum` int(5) DEFAULT 0,
  `Buploaddate` date DEFAULT '1993-11-08',
  PRIMARY KEY (`Bid`) USING BTREE,
  INDEX `B_FSid`(`Sid`) USING BTREE,
  CONSTRAINT `B_FSid` FOREIGN KEY (`Sid`) REFERENCES `shop` (`Sid`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 104 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

