-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user`  (
  `Uid` int(10) NOT NULL AUTO_INCREMENT,
  `Upassword` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `Uavatar` varchar(40) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT 'avatar.jpg',
  `Unickname` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '匿名',
  `Urealname` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Usex` varchar(1) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT 'M',
  `Uaddress` varchar(80) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Uphone` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Uemail` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT '',
  `Ubirthday` date DEFAULT NULL,
  PRIMARY KEY (`Uid`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 24 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

